import React, { useState } from "react";

const Main = () => {
  const [inputData, setinputData] = useState("");
  const [items, setitems] = useState([]);

  const addItems = () => {
    if (!inputData) {

    }
    else {
      setitems([...items, inputData]);
      setinputData("")
    }
    ;
  }
  const deleteitem = (id) => {
    const newitem = items.filter((v, Idn) => {
      return Idn != id
    })
    setitems(newitem);
  }
  const remove = () => {
    setitems([]);
  }
  return (
    <>
      <div>
        <div>
          <h1>Todo aap</h1>
          <div>
            <input type="text" placeholder="enter items to add" value={inputData} onChange={(e) => setinputData(e.target.value)}></input>
            <button onClick={addItems}>+</button>
          </div>
          <div className="data" >
            {items.map((v, Idn) => {
              return (
                <div key={Idn}>
                  <ol>
                    <li>{v}
                      <button onClick={() => deleteitem(Idn)}>Remove</button>
                    </li>
                  </ol>
                </div>

              )
            }


            )}

          </div>
          <div>
            <button onClick={remove}>delete all</button>
          </div>
        </div>
      </div>


    </>
  );
}
export default Main;
