import React, { useState } from "react";

const Main = () => {

const [inputlist,setinputlist]=useState("");
const [items,setitems]=useState([]);

const itemEvent =(event) =>{
  setinputlist(event.target.value);
};
const listofitems = () =>{
  setitems((old)=>{
    return [...old,inputlist];
  });
};

  return (
    <>
   <div>
     <div>
       <h1>Todo aap</h1>
       <div>
        <input type="text" placeholder="enter items to add" onChange={itemEvent}></input>
        <button onclick={listofitems}>+</button>
        <ol>
          {items.map((vals) => {
            return <li>{vals}</li> 
          })}
          {/* <li>{inputlist}</li> */}
        </ol>
       </div>
     </div>
   </div>
  
  </>
  );
}
export default Main;
