import React, { useState } from "react";
import Button from '@material-ui/core/Button';
import AddCircleIcon from '@material-ui/icons/AddCircle';

const Main = () => {
  const [inputData, setinputData] = useState("");
  const [items, setitems] = useState([]);

  const addItems = () => {
    if (!inputData) {

    }
    else {
      setitems([...items, inputData]);
      setinputData("")
    }
    ;
  }
  const deleteitem = (id) => {
    const newitem = items.filter((v, Idn) => {
      return Idn !== id
    })
    setitems(newitem);
  }
  const remove = () => {
    setitems([]);
  }
  return (
    <>
      <div>
        <div className="main-div">
          <h1 className="header">Todo aap</h1>
          <div >
            <input className="inpt" type="text" placeholder="enter items to add" value={inputData} onChange={(e) => setinputData(e.target.value)}></input>
              <Button className="btn"  onClick={addItems}> AddCircleIcon</Button>
           
          </div>
          <div className="data" >
            {items.map((v, Idn) => {
              return (
                <div key={Idn}>
                  <ol>
                    <li>{v}
                    <Button  className="btn" onClick={() => deleteitem(Idn)}>Remove</Button>
                    </li>
                   
                  </ol>
                </div> )})}
                <Button onClick={remove} ><span className="btn">delete all</span></Button>
          </div>
        </div>
      </div>


    </>
  );
}
export default Main;
